mapper.select:
SELECT A.*, M.name AS writerName, SUM(R.point) AS sumReactionPoint FROM article AS A INNER JOIN `member` AS M ON A.memberId = M.id LEFT JOIN reactionPoint AS R ON A.id = R.relId WHERE 1 = 1
AND A.title LIKE CONCAT('%',#{keyWord},'%') AND A.body LIKE CONCAT('%',#{keyWord},'%') AND (A.title LIKE CONCAT('%',#{keyWord},'%') OR A.body LIKE CONCAT('%',#{keyWord},'%'))
AND A.boardId = #{boardId}
GROUP BY A.id ORDER BY A.id DESC LIMIT #{limitStart}, #{itemsInAPage}

mapper.update:
UPDATE article 

	updateDate = NOW(),

		title = #{title}, 


		`body` = #{body}


	WHERE id = #{id}

UPDATE article
SET hit = hit + 1
WHERE id = #{id}

mapper.select:
SELECT COUNT(*)
FROM article
WHERE memberId = #{memberId}
