mapper.select:
WITH AM AS( SELECT A.*, M.name AS writerName FROM article AS A INNER JOIN `member` AS M ON A.memberId = M.id WHERE 1 = 1
AND A.title LIKE CONCAT('%',#{keyWord},'%') AND A.body LIKE CONCAT('%',#{keyWord},'%') AND (A.title LIKE CONCAT('%',#{keyWord},'%') OR A.body LIKE CONCAT('%',#{keyWord},'%'))
AND A.boardId = #{boardId}
) SELECT AM.* , IFNULL(SUM(R.point), 0) AS sumReactionPoint , IFNULL(SUM(IF(R.point > 0, R.point, 0)),0) AS likeReactionPoint , IFNULL(SUM(IF(R.point < 0, R.point, 0)),0) AS hateReactionPoint FROM AM LEFT JOIN reactionPoint AS R ON R.relTypeCode = 'article' AND R.relId = AM.id GROUP BY AM.id ORDER BY AM.id DESC LIMIT #{limitStart}, #{itemsInAPage}

mapper.update:
UPDATE article 

	updateDate = NOW(),

		title = #{title}, 


		`body` = #{body}


	WHERE id = #{id}

UPDATE article
SET hit = hit + 1
WHERE id = #{id}

mapper.select:
SELECT COUNT(*)
FROM article
WHERE memberId = #{memberId}
